changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.get_attribute_type(attribute_id text)
  RETURNS character varying AS
$BODY$
DECLARE
    _attribute ruleform.attribute%ROWTYPE;
BEGIN
    SELECT * INTO STRICT _attribute FROM attribute WHERE id = attribute_id;
    RETURN _attribute.value_type;
END;
$BODY$
  LANGUAGE plpgsql STABLE STRICT
  COST 100;
ALTER FUNCTION ruleform.get_attribute_type(text)
  OWNER TO core;
