changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.generate_command_line(p_event_rule bigint)
  RETURNS text AS
$BODY$
DECLARE
    _attribute_set ruleform.attribute.id%TYPE;
    _associated_parameter_attribute CONSTANT ruleform.attribute.id%TYPE NOT NULL := attribute_id('Associated Parameter Set');
    _includes_relationship CONSTANT ruleform.relationship.id%TYPE NOT NULL := relationship_id('includes');

    _parameter_line VARCHAR := '';
    _cursor REFCURSOR;
    _attribute_id ruleform.attribute.id%TYPE;
    _event_attribute_rule ruleform.event_attribute.id%TYPE;
     _parameter_value VARCHAR;

    _prototype_relationship CONSTANT ruleform.relationship.id%TYPE NOT NULL := relationship_id('prototype');

    _executable ruleform.agency%ROWTYPE;

BEGIN

    SELECT r.* INTO _executable
    FROM ruleform.event_attribute AS ea
        JOIN agency AS r ON r.id = ea.agency_value
    WHERE ea.event = p_event_rule
      AND ea.attribute = attribute_id('Executable');

    _parameter_line = _executable.name;

    SELECT ea.attribute_value INTO STRICT _attribute_set 
    FROM 
        ruleform.event_attribute AS ea
        JOIN event_network AS en ON en.child = ea.event
    WHERE en.parent = p_event_rule 
      AND en.relationship = _prototype_relationship
      AND ea.attribute = _associated_parameter_attribute;

    OPEN _cursor FOR 
        SELECT child 
        FROM ruleform.attribute_network 
        WHERE parent = _attribute_set 
          AND relationship = _includes_relationship;
    LOOP FETCH _cursor INTO _attribute_id;
        EXIT WHEN NOT FOUND;

        SELECT id INTO _event_attribute_rule FROM event_attribute WHERE attribute = _attribute_id;
        IF _event_attribute_rule IS NOT NULL THEN
            _parameter_value := event_attribute_to_parameter_value(_event_attribute_rule);
            RAISE NOTICE 'Parameter-value pair is "%"', _parameter_value;
            _parameter_line := _parameter_line || ' ' || _parameter_value;
        END IF;
    END LOOP;
    CLOSE _cursor;

    RETURN trim(_parameter_line);

END;
$BODY$
  LANGUAGE plpgsql STABLE
  COST 100;
ALTER FUNCTION ruleform.generate_command_line(bigint)
  OWNER TO core;
