changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.get_location_context(p_location_id bigint)
  RETURNS ruleform.location_context AS
$BODY$
    SELECT c.*
    FROM ruleform.location AS l
    JOIN ruleform.location_context AS c ON l.context = c.id
    WHERE l.id = $1;
$BODY$
  LANGUAGE sql STABLE STRICT
  COST 100;
ALTER FUNCTION ruleform.get_location_context(bigint)
  OWNER TO core;
COMMENT ON FUNCTION ruleform.get_location_context(bigint) IS 'Retrieves the unique Location Context of the given Location';
