changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.np_propagate_all()
  RETURNS void AS
$BODY$
DECLARE
    t VARCHAR;
    tables VARCHAR[] := ARRAY['ruleform.attribute_network','ruleform.resource_network', 'ruleform.event_network', 'ruleformlocation_network', 'ruleform.product_network'];
BEGIN
    FOR i IN 1..array_upper(tables,1) LOOP
        t := tables[i];
        RAISE NOTICE 'Propagating %',t;
        PERFORM np_propagate(t);
    END LOOP;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.np_propagate_all()
  OWNER TO core;
COMMENT ON FUNCTION ruleform.np_propagate_all() IS 'Propagates all networks in a single command';
