changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.status_code_id(character varying)
  RETURNS bigint AS
$BODY$
    SELECT id FROM ruleform.status_code WHERE name = $1;
$BODY$
  LANGUAGE sql STABLE
  COST 100;
ALTER FUNCTION ruleform.status_code_id(character varying)
  OWNER TO core;
COMMENT ON FUNCTION ruleform.status_code_id(character varying) IS 'Retrieves the ID of the Status Code with the given name, or NULL if there is nothing with that name.';
