changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.status_code_name(bigint)
  RETURNS character varying AS
$BODY$
    SELECT name FROM ruleform.status_code WHERE id = $1;
$BODY$
  LANGUAGE sql STABLE
  COST 100;
ALTER FUNCTION ruleform.status_code_name(bigint)
  OWNER TO core;
COMMENT ON FUNCTION ruleform.status_code_name(bigint) IS 'Retrieves the name of the Status Code with the given ID, or NULL if there is nothing with that ID.';
