changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.translation(event character varying, agency character varying, seq character varying)
  RETURNS character varying AS
$BODY$
DECLARE
    size CONSTANT INTEGER NOT NULL := 3;
    result VARCHAR := '';
    chunk VARCHAR;
    product VARCHAR;
BEGIN

    FOR i IN 1..length(seq) BY size LOOP
        product := substring(seq, i, size);
        RAISE NOTICE 'Product = %', product;
        chunk := ruleform.transform(event, agency, product);
        RAISE NOTICE 'Chunk is ''%''', chunk;
        IF chunk IS NULL THEN
            RAISE EXCEPTION 'Given sequence contains Product ''%'' which returned null!', product;
        END IF;
        result := result || chunk;
    END LOOP;

    RETURN result;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.translation(character varying, character varying, character varying)
  OWNER TO core;
