changeSet.createView:
SELECT classifier.name as classifier, classification.name as classification, bridge.name as child_relationship, authorized_rel.name as auth_relationship, authorized_parent.name as auth_parent
from ruleform.interval_network_authorization auth join ruleform.interval classification on auth.classification = classification.id join ruleform.relationship classifier on auth.classifier = classifier.id join ruleform.relationship bridge on auth.child_relationship = bridge.id join ruleform.interval authorized_parent on auth.authorized_parent = authorized_parent.id join ruleform.relationship authorized_rel on auth.authorized_relationship = authorized_rel.id;
ALTER TABLE readable.interval_network_authorization OWNER TO core;
