changeSet.sql:
            CREATE OR REPLACE VIEW readable.unit_values AS 
			 SELECT uv.id, u.name AS unit, uv.sequence_number, uv.integer_value, uv.numeric_value, uv.text_value, uv.boolean_value, uv.notes, up.name AS updated_by, uv.version
			   FROM unit_values uv
			   JOIN unit u ON u.id = uv.unit
			   JOIN agency up ON up.id = uv.updated_by;

			ALTER TABLE readable.unit_values
			  OWNER TO core;
			GRANT ALL ON TABLE readable.unit_values TO core;
			GRANT SELECT ON TABLE readable.unit_values TO public;
