changeSet.sql:
CREATE OR REPLACE VIEW readable.location_network_authorization AS SELECT classification.name as classification, classifier.name as classifier, bridge.name as child_relationship, authorized_rel.name as auth_relationship, authorized_parent.name as auth_parent
from ruleform.location_network_authorization auth join ruleform.location classifier on auth.classifier = classifier.id join ruleform.relationship classification on auth.classification = classification.id join ruleform.relationship bridge on auth.child_relationship = bridge.id join ruleform.location authorized_parent on auth.authorized_parent = authorized_parent.id join ruleform.relationship authorized_rel on auth.authorized_relationship = authorized_rel.id;
ALTER TABLE readable.location_network_authorization OWNER TO core;
