changeSet.sql:
            CREATE OR REPLACE VIEW readable.protocol_attribute AS 
			 SELECT base_table.id, base_table.protocol, at.name AS attribute, 
			        base_table.sequence_number,
			        base_table.text_value, base_table.numeric_value, 
			        base_table.integer_value, u.name AS unit, 
			        base_table.notes,
			        up.name AS updated_by, base_table.update_date
			   FROM ruleform.protocol_attribute base_table
			   JOIN ruleform.attribute at ON base_table.attribute = at.id
			   LEFT JOIN ruleform.unit u ON base_table.unit = u.id
			   JOIN ruleform.agency up ON base_table.updated_by = up.id;

			ALTER TABLE readable.protocol_attribute
			  OWNER TO core;
			GRANT ALL ON TABLE readable.protocol_attribute TO core;
			GRANT SELECT ON TABLE readable.protocol_attribute TO core_read_write;
			GRANT SELECT ON TABLE readable.protocol_attribute TO core_read_only;
