changeSet.sql:
            CREATE OR REPLACE VIEW readable.status_code AS 
			 SELECT base_table.id, base_table.name, base_table.description, base_table.notes, base_table.pinned, base_table.fail_parent, base_table.propagate_children, up.name AS updated_by, base_table.update_date
			   FROM ruleform.status_code base_table
			   JOIN ruleform.agency up ON base_table.updated_by = up.id;

			ALTER TABLE readable.status_code
			  OWNER TO core;
			GRANT ALL ON TABLE readable.status_code TO core;
			GRANT SELECT ON TABLE readable.status_code TO core_read_write;
			GRANT SELECT ON TABLE readable.status_code TO core_read_only;
