changeSet.sql:
            CREATE OR REPLACE VIEW readable.status_code_sequencing AS 
			 SELECT base_table.id, s.name AS service, pc.name AS parent_code, cc.name AS child_code, base_table.notes, up.name AS updated_by, base_table.version
			   FROM status_code_sequencing base_table
			   JOIN product s ON base_table.service = s.id
			   JOIN status_code pc ON base_table.parent_code = pc.id
			   JOIN status_code cc ON base_table.child_code = cc.id
			   JOIN agency up ON base_table.updated_by = up.id;

			ALTER TABLE readable.status_code_sequencing
			  OWNER TO core;
			GRANT ALL ON TABLE readable.status_code_sequencing TO core;
			GRANT SELECT ON TABLE readable.status_code_sequencing TO core_read_write;
			GRANT SELECT ON TABLE readable.status_code_sequencing TO core_read_only;
