changeSet.sql:
CREATE DOMAIN ordinal_number
  AS integer
   CONSTRAINT valid_ordinal_number CHECK ((VALUE > 0));
ALTER DOMAIN ordinal_number
  OWNER TO core;

CREATE DOMAIN relational_operator
  AS character(2)
  COLLATE pg_catalog."default"
   CONSTRAINT valid_relational_operator CHECK ((VALUE = ANY (ARRAY['='::bpchar, '<'::bpchar, '>'::bpchar, '<='::bpchar, '>='::bpchar])));
ALTER DOMAIN relational_operator
  OWNER TO core;

CREATE DOMAIN sequence_number
  AS integer
  DEFAULT 1
   CONSTRAINT sequence_number_check CHECK ((VALUE >= 0));
ALTER DOMAIN sequence_number
  OWNER TO core;
