changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.forbid_foreign_key_updates()
  RETURNS trigger AS
$BODY$
BEGIN
    IF NEW.parent != OLD.parent
        OR NEW.relationship != OLD.relationship
        OR NEW.child != OLD.child
    THEN RAISE EXCEPTION 'Cannot update parent, relationship, or child values.';
    END IF;

END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.forbid_foreign_key_updates()
  OWNER TO core;
COMMENT ON FUNCTION ruleform.forbid_foreign_key_updates() IS 'Prevents network foreign keys from being updated.';
