changeSet.sql:
            CREATE OR REPLACE VIEW readable.coordinate AS 
			 SELECT c.id, c.name, k.name AS kind, c.description, c.notes, c.pinned, r.name AS research, up.name AS updated_by, c.update_date
			   FROM ruleform.coordinate c
			   LEFT JOIN ruleform.research r ON r.id = c.research
			   JOIN ruleform.agency up ON up.id = c.updated_by
			   JOIN ruleform.coordinate_kind k ON k.id = c.kind;

			ALTER TABLE readable.coordinate
			  OWNER TO core;
			GRANT ALL ON TABLE readable.coordinate TO core;
			GRANT SELECT ON TABLE readable.coordinate TO public;
