changeSet.sql:
            CREATE OR REPLACE VIEW readable.coordinate_kind_definition AS 
			 SELECT d.id, k.name AS kind, d.sequence_number, a.name AS attribute, sub.name AS subordinate_coordinate_kind, d.notes, r.name AS research, up.name AS updated_by, d.update_date
			   FROM ruleform.coordinate_kind_definition d
			   JOIN ruleform.coordinate_kind k ON k.id = d.kind
			   LEFT JOIN ruleform.attribute a ON a.id = d.attribute
			   LEFT JOIN ruleform.coordinate_kind sub ON sub.id = d.subordinate_coordinate_kind
			   LEFT JOIN ruleform.research r ON r.id = d.research
			   JOIN ruleform.agency up ON up.id = d.updated_by;

			ALTER TABLE readable.coordinate_kind_definition
			  OWNER TO core;
			GRANT ALL ON TABLE readable.coordinate_kind_definition TO core;
			GRANT SELECT ON TABLE readable.coordinate_kind_definition TO public;
