changeSet.sql:
            CREATE OR REPLACE VIEW readable.entity_location_network AS 
			 SELECT n.id, res.name AS resource, b.name AS entity, cb.name AS contextual_entity, c.name AS coordinate, n.notes, r.name AS research, up.name AS updated_by, n.update_date
			   FROM ruleform.entity_location_network n
			   JOIN ruleform.resource res ON res.id = n.resource
			   JOIN ruleform.entity b ON b.id = n.entity
			   JOIN ruleform.entity cb ON cb.id = n.contextual_entity
			   JOIN ruleform.coordinate c ON c.id = n.coordinate
			   LEFT JOIN ruleform.research r ON r.id = n.research
			   JOIN ruleform.resource up ON up.id = n.updated_by;

			ALTER TABLE readable.entity_location_network
			  OWNER TO core;
			GRANT ALL ON TABLE readable.entity_location_network TO core;
			GRANT SELECT ON TABLE readable.entity_location_network TO public;
