changeSet.sql:
            CREATE OR REPLACE VIEW readable.job_chronology AS 
			 SELECT base.id, base.job, base.time_stamp, s.name AS status, base.notes, res.name AS research, up.name AS updated_by, base.update_date
			   FROM ruleform.job_chronology base
			   JOIN ruleform.status_code s ON base.status = s.id
			   LEFT JOIN ruleform.research res ON base.research = res.id
			   JOIN ruleform.resource up ON base.updated_by = up.id;

			ALTER TABLE readable.job_chronology
			  OWNER TO core;
			GRANT ALL ON TABLE readable.job_chronology TO core;
			GRANT SELECT ON TABLE readable.job_chronology TO core_read_write;
			GRANT SELECT ON TABLE readable.job_chronology TO core_read_only;
