changeSet.sql:
            CREATE OR REPLACE VIEW readable.product_child_sequencing_authorization AS 
			 SELECT base_table.id, p.name AS parent, sc.name AS status_code, 
			        base_table.sequence_number, nc.name AS next_child, 
			        ncs.name AS next_child_status, base_table.notes, 
			        up.name AS updated_by, base_table.update_date
			   FROM ruleform.product_child_sequencing_authorization base_table
			   JOIN ruleform.product p ON base_table.parent = p.id
			   JOIN ruleform.status_code sc ON base_table.status_code = sc.id
			   LEFT JOIN ruleform.product nc ON base_table.next_child = nc.id
			   LEFT JOIN ruleform.status_code ncs ON base_table.next_child_status = ncs.id
			   JOIN ruleform.agency up ON base_table.updated_by = up.id;

			ALTER TABLE readable.product_child_sequencing_authorization
			  OWNER TO core;
			GRANT ALL ON TABLE readable.product_child_sequencing_authorization TO core;
			GRANT SELECT ON TABLE readable.product_child_sequencing_authorization TO core_read_write;
			GRANT SELECT ON TABLE readable.product_child_sequencing_authorization TO core_read_only;
