changeSet.sql:
            CREATE OR REPLACE VIEW readable.unit_values AS 
			 SELECT uv.id, u.name AS unit, uv.sequence_number, uv.integer_value, uv.numeric_value, uv.text_value, uv.boolean_value, bv.name AS entity_value, uv.notes, r.name AS research, up.name AS updated_by, uv.update_date
			   FROM ruleform.unit_values uv
			   JOIN ruleform.unit u ON u.id = uv.unit
			   LEFT JOIN ruleform.entity bv ON bv.id = uv.entity_value
			   LEFT JOIN ruleform.research r ON r.id = uv.research
			   JOIN ruleform.resource up ON up.id = uv.updated_by;

			ALTER TABLE readable.unit_values
			  OWNER TO core;
			GRANT ALL ON TABLE readable.unit_values TO core;
			GRANT SELECT ON TABLE readable.unit_values TO public;
