changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform._contains_id(value bigint, key text)
  RETURNS boolean AS
$BODY$
DECLARE
    _temp_table_name CONSTANT TEXT NOT NULL := '__shared_temp_' || key;
    _query CONSTANT TEXT NOT NULL := 'SELECT EXISTS( SELECT * FROM _ids(' || quote_literal(key) || ') WHERE _ids = ' || value || ' )';
    _result BOOLEAN;
BEGIN
    EXECUTE _query INTO STRICT _result;
    RETURN _result;
END;
$BODY$
  LANGUAGE plpgsql STABLE SECURITY DEFINER
  COST 100;
COMMENT ON FUNCTION ruleform._contains_id(bigint, text) IS 'Returns TRUE if the given value exists in the temporary table for "key"; FALSE otherwise';
