CREATE OR REPLACE FUNCTION ruleform.location_context_name(location_context_id bigint)
  RETURNS character varying AS
$BODY$
SELECT name FROM ruleform.location_context WHERE id = $1;
$BODY$
  LANGUAGE sql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.location_context_name(bigint)
  OWNER TO core;
