changeSet.sql:
CREATE TABLE ruleform.research
(
id bigserial NOT NULL, -- Unique identifier for research flags
name character varying NOT NULL, -- Short name to easily refer to a research flag
description character varying, -- Longer verbose description of the meaning of the research flag
CONSTRAINT research_pkey PRIMARY KEY (id ),
CONSTRAINT research_name_key UNIQUE (name )
)
WITH (
OIDS=FALSE
);
COMMENT ON TABLE ruleform.research 
    IS 'Contains various ''research flags'', indicating reasons for why a particular rule may need to be investigated.';
COMMENT ON COLUMN ruleform.research.id
    IS 'Unique identifier for research flags';
COMMENT ON COLUMN ruleform.research.name 
    IS 'Short name to easily refer to a research flag';
COMMENT ON COLUMN ruleform.research.description 
    IS 'Longer verbose description of the meaning of the research flag';
