changeSet.sql:
CREATE TABLE ruleform.status_code
(
  id bigserial NOT NULL, -- Unique identifier
  name character varying NOT NULL, -- The unique name of a status code
  description character varying, -- A free-form textual description of a status code
  notes character varying, -- Additional notes regarding usage of the status code
  pinned boolean NOT NULL DEFAULT FALSE, -- Indicates that a rule has been pinned, restricting changes to the rule
  fail_parent boolean NOT NULL DEFAULT TRUE, -- Setting a job to this status code will trigger a cascading failure up to its parent
  propagate_children boolean NOT NULL DEFAULT TRUE, -- Setting a job to this status code will trigger the propagation of child (implicit) jobs
  updated_by bigint NOT NULL, -- The agency that last updated this rule
  update_date timestamp with time zone NOT NULL DEFAULT ('now'::text)::timestamp(6) with time zone, -- The date this rule was last changed
  CONSTRAINT status_code_pkey PRIMARY KEY (id ),
  CONSTRAINT status_code_updated_by_fkey FOREIGN KEY (updated_by)
      REFERENCES ruleform.agency (id) MATCH SIMPLE
      ON UPDATE CASCADE ON DELETE RESTRICT DEFERRABLE INITIALLY IMMEDIATE
)
WITH (
  OIDS=FALSE
);
COMMENT ON TABLE ruleform.status_code
      IS 'Existential ruleform for all status codes (event outcomes)';
COMMENT ON COLUMN ruleform.status_code.id 
        IS 'Unique identifier';
COMMENT ON COLUMN ruleform.status_code.name 
        IS 'The unique name of a status code';
COMMENT ON COLUMN ruleform.status_code.description 
        IS 'A free-form textual description of a status code';
COMMENT ON COLUMN ruleform.status_code.notes 
        IS 'Additional notes regarding usage of the status code';
COMMENT ON COLUMN ruleform.status_code.pinned 
        IS 'Indicates that a rule has been pinned, restricting changes to the rule';
COMMENT ON COLUMN ruleform.status_code.fail_parent 
        IS 'Setting a job to this status code will trigger a cascading failure up to its parent';
COMMENT ON COLUMN ruleform.status_code.propagate_children 
        IS 'Setting a job to this status code will trigger the propagation of child (implicit) jobs';
COMMENT ON COLUMN ruleform.status_code.updated_by 
        IS 'The agency that last updated this rule';
COMMENT ON COLUMN ruleform.status_code.update_date 
        IS 'The date this rule was last changed';
