changeSet.sql:
CREATE TABLE ruleform.workspace_authorization ( id bigserial NOT NULL, product bigint NOT NULL, authorization_type character varying NOT NULL, relationship bigint, status_code bigint, unit bigint, description character varying, notes character varying, updated_by bigint NOT NULL, update_date timestamp with time zone NOT NULL DEFAULT ('now'::text)::timestamp(6) with time zone,
CONSTRAINT workspace_authorization_pkey PRIMARY KEY (id ), CONSTRAINT workspace_authorization_product_fkey FOREIGN KEY (product) REFERENCES ruleform.product (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE NO ACTION DEFERRABLE INITIALLY IMMEDIATE, CONSTRAINT workspace_authorization_relationship_fkey FOREIGN KEY (relationship) REFERENCES ruleform.relationship (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE NO ACTION DEFERRABLE INITIALLY IMMEDIATE, CONSTRAINT workspace_authorization_status_code_fkey FOREIGN KEY (status_code) REFERENCES ruleform.status_code (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE NO ACTION DEFERRABLE INITIALLY IMMEDIATE, CONSTRAINT workspace_authorization_unit_fkey FOREIGN KEY (unit) REFERENCES ruleform.unit (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE NO ACTION DEFERRABLE INITIALLY IMMEDIATE, CONSTRAINT workspace_authorization_updated_by_fkey FOREIGN KEY (updated_by) REFERENCES ruleform.agency (id) MATCH SIMPLE ON UPDATE CASCADE ON DELETE NO ACTION DEFERRABLE INITIALLY IMMEDIATE, CONSTRAINT workspace_authorization_only_one_child CHECK (ruleform.null_zero(relationship) + ruleform.null_zero(status_code) + ruleform.null_zero(unit) = 1)
) WITH ( OIDS=FALSE ); COMMENT ON TABLE ruleform.workspace_authorization IS 'Authorizations to join networks of ruleforms';
