changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.restrict_pinned()
  RETURNS trigger AS
$BODY$
BEGIN
    IF (TG_OP = 'UPDATE') THEN
        IF (OLD.pinned = 0) OR (NEW.pinned = 0) THEN
            RETURN NEW;
        ELSE 
            IF (OLD.pinned = 1) THEN 
                RAISE EXCEPTION 'Cannot update pinned % %', TG_RELNAME, OLD.name;
            END IF;
        END IF;
    ELSE
        IF (TG_OP = 'DELETE') THEN
            IF OLD.pinned = 1 THEN
                RAISE EXCEPTION 'Cannot delete pinned % ''%''', TG_RELNAME, OLD.name;
            ELSE
                RETURN OLD;
            END IF;
        END IF;
    END IF;

    RETURN NULL;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.restrict_pinned()
  OWNER TO core;
COMMENT ON FUNCTION ruleform.restrict_pinned() IS 'Trigger function used to ensure that pinned rules are not erroneously modified or deleted';
