changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.set_update_date()
  RETURNS trigger AS
$BODY$
BEGIN
    NEW.update_date := current_timestamp;
    RETURN NEW;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.set_update_date()
  OWNER TO core;
COMMENT ON FUNCTION ruleform.set_update_date() IS 'Trigger function used to automatically set the update_date field of a ruleform to the current time.';
