changeSet.sql:
CREATE OR REPLACE FUNCTION ruleform.valid_resource_attribute()
  RETURNS trigger AS
$BODY$
DECLARE
    _type TEXT;
BEGIN
    IF ( (TG_TABLE_SCHEMA != 'ruleform') AND (TG_TABLE_NAME != 'resource_attribute') ) THEN
        RAISE EXCEPTION 'Trigger "%" is improperly configured; it must only be attached to the ruleform.resource_attribute table', TG_NAME;
    END IF;

    IF ( (TG_WHEN != 'AFTER') OR (TG_OP NOT IN ('INSERT','UPDATE') )) THEN
        RAISE EXCEPTION 'Trigger "%" on table "%" is improperly configured; it must only run after INSERT or UPDATE operations!', TG_NAME, TG_TABLE_SCHEMA || '.' || TG_TABLE_NAME;
    END IF;

    SELECT ruleform.get_attribute_type('resource', NEW.attribute) INTO STRICT _type;
    IF (_type = 'text') THEN
        IF NEW.text_value IS NULL THEN
            RAISE EXCEPTION 'Must supply a text value for a text-valued Attribute!';
        END IF;
    ELSEIF (_type = 'numeric') THEN
        IF NEW.numeric_value IS NULL THEN
            RAISE EXCEPTION 'Must supply a numeric value for a numeric-valued Attribute!';
        END IF;
    ELSEIF (_type = 'boolean') THEN
        IF NEW.boolean_value IS NULL THEN
            RAISE EXCEPTION 'Must supply a boolean value for a boolean-valued Attribute!';
        END IF;
    ELSEIF (_type = 'resource') THEN
        IF NEW.resource_value IS NULL THEN
            RAISE EXCEPTION 'Must supply a Resource value for a Resource-valued Attribute!';
        END IF;
    ELSEIF (_type = 'entity') THEN
        IF NEW.entity_value IS NULL THEN
            RAISE EXCEPTION 'Must supply a Entity value for a Entity-valued Attribute!';
        END IF;
    ELSEIF (_type = 'event') THEN
        IF NEW.event_value IS NULL THEN
            RAISE EXCEPTION 'Must supply an Event value for a Event-valued Attribute!';
        END IF;
    ELSEIF (_type = 'attribute') THEN
        IF NEW.attribute_value IS NULL THEN
            RAISE EXCEPTION 'Must supply an Attribute value for a Attribute-valued Attribute!';
        END IF;
    ELSE
        RAISE EXCEPTION '% does not know how to handle an attribute of type %', TG_TABLE_SCHEMA || '.' || TG_TABLE_NAME, _type;
    END IF;

    RETURN NEW;

END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
ALTER FUNCTION ruleform.valid_resource_attribute()
  OWNER TO core;
