changeSet.sql:
CREATE OR REPLACE FUNCTION ensure_domain(existential uuid, d existential_domain)
    RETURNS integer AS $$
BEGIN 
    SELECT id FROM existential WHERE domain = d AND id = existential;
    IF NOT FOUND THEN
        RAISE EXCEPTION 'Existential % is not the correct domain %s', existential, d;
    END IF; 
END
$$ LANGUAGE plpgsql VOLATILE
    COST 100;
